import React, {useState} from 'react';
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from '../ExpenseFilter/ExpensesFilter';
import "./Expenses.css";

function Expenses(props){
  const[filteredExpenses, setFilteredExpenses] = useState("2022");
  const[selectedFilter, setSelectedFilter] = useState("2022");
  const changeFilterHandler = (year) =>{
    setSelectedFilter(year);
    setFilterExpenses(year);
  }


  const setFilterExpenses = (filterBy) =>{
    setFilteredExpenses(filteredExpenses.filter(filterExpenses));
  }
  const filterExpenses = (expenseItem, filterBy) =>{
    return expenseItem.date.getFullYear() === filterBy;
  }

 return(
    <Card className="expenses">
    <ExpensesFilter selected={selectedFilter}   onChangeFilter = {changeFilterHandler}/>
    {filteredExpenses.map((expense) => (
      <ExpenseItem
      key = {expense.id}
       title = {expense.title} 
       amount = {expense.amount} 
       date = {expense.date}/>
    ))}
    </Card>
 );
}

export default Expenses;
